var IDM=IDM||{};function updateContent(){document.querySelectorAll("[data-translation-key]").forEach(n=>{n.innerHTML=i18next.t(n.dataset.translationKey)})}function changeLng(n){i18next.changeLanguage(n)}$(document).ready(function(){document.getElementById("toggle-button").addEventListener("click",function(){"es-flag"===document.getElementById("toggle").className?changeLng("es"):changeLng("en")});var n=navigator.language||navigator.userLanguage;i18next.init({lng:n,fallbackLng:["en"],debug:!0,resources:{en:{translation:IDM.en},es:{translation:IDM.es}}},function(n,e){updateContent()})}),i18next.on("languageChanged",()=>{updateContent()});